import React, { useState } from "react";
import {
  StyleSheet,
  View,
  TextInput,
  TouchableWithoutFeedback,
  Keyboard,
  KeyboardAvoidingView,
  Platform,
  Alert,
  TouchableOpacity,
  Text,
  ImageBackground,
  Image,
} from "react-native";

export default function Registration({ navigation }) {
  // const [name, setName] = useState("");
  // const [mail, setMail] = useState("");
  // const [password, setPassword] = useState("");
  const [isShowKeyboard, setIsShowKeyboard] = useState(false);
  const [state, setState] = useState(initialState);

  const initialState = {
    name: "",
    mail: "",
    password: "",
  };

  // const nameHandler = (text) => setName(text);
  // const mailHandler = (text) => setMail(text);
  // const passwordHandler = (text) => setPassword(text);

  const handleSubmit = () => {
    setIsShowKeyboard(false);
    Alert.alert("Credentials", `${name} ${mail} ${password}`);

    setState(initialState);
  };

  const keyBoardHide = () => {
    setIsShowKeyboard(false)
    Keyboard.dismiss()
    handleSubmit()
  }

  return (
    <TouchableWithoutFeedback onPress={Keyboard.dismiss}>
      <View style={styles.container}>
      <ImageBackground
          source={require("../../assets/photo-BG-2x.jpg")}
          style={styles.image}
        >

        <KeyboardAvoidingView
          behavior={Platform.OS == "ios" ? "padding" : "height"}
        >
            <View
                style={{
                  ...styles.formWrapper,

                  ...Platform.select({
                    ios: {
                      paddingBottom: isShowKeyboard ? 140 : 20,
                    },
                    android: {
                      marginTop: isShowKeyboard ? -100 : 0,
                    },
                  }),
                }} 
              >
                <View style={styles.imgBox}>
                  <Image
                    style={styles.icon}
                    source={require("../../assets/plus.png")}
                  />
                </View>
            <Text style={styles.title}>Регистрация</Text>
            <View style={{
                    ...styles.form,
                    paddingBottom: isShowKeyboard ? 32 : 45,
                  }}>
          <TextInput
            value={name}
            // onChangeText={nameHandler}
            placeholder="Логин"
            style={styles.input}
            onFocus={() => {
              setIsShowKeyboard(true)
            }}
            onChangeText={(value) => 
            setState((prevState) => ({
              ...prevState,
              mail: value,
            }))}
          />
          <TextInput
            value={mail}
            onChangeText={mailHandler}
            placeholder="Адрес электронной почты"
            style={styles.input}
            onFocus={() => {
              setIsShowKeyboard(true)
            }}
          />
          <TextInput
            value={password}
            onChangeText={passwordHandler}
            placeholder="Пароль"
            secureTextEntry={true}
            style={styles.input}
            onFocus={() => {
              setIsShowKeyboard(true)
            }}
          />
          </View>
          <TouchableOpacity
                    style={styles.button}
                    activeOpacity={0.8}
                    onPress={keyBoardHide}
                  >
                    <Text style={styles.buttonText}>Зарегистрироваться</Text>
            </TouchableOpacity>
            <TouchableOpacity onPress={() => navigation.navigate("Login")}>
                    <Text style={styles.aside}>Уже есть аккаунт? Войти</Text>
            </TouchableOpacity>
          </View>
        </KeyboardAvoidingView>


        </ImageBackground>
      </View>
    </TouchableWithoutFeedback>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,

  },
  formWrapper: {
     alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#ecf0f1",
    paddingTop: 100,

    backgroundColor: "#FFFFFF",
    borderTopRightRadius: 25,
    borderTopLeftRadius: 25,
    justifyContent: "center",
  },
  input: {
    width: 300,
    height: 50,
    padding: 10,
    borderWidth: 1,
    borderColor: "black",
    marginBottom: 10,
    borderRadius: 15,
  },
  image: {
    flex: 1,
    resizeMode: "cover",
    justifyContent: "flex-end",
  },
  button: {
    width: 300,
    backgroundColor: "#FF6C00",
    height: 61,
    borderRadius: 100,
    justifyContent: "center",
    alignItems: "center",
  },
  buttonText: {
    lineHeight: 19,
    color: "#FFFFFF",
  },
  aside: {
    lineHeight: 19,
    marginTop: 16,
    textAlign: "center",
    color: "#1B4371",
  },
  icon: {
    position: "absolute",
    left: "90%",
    top: "65%",
    width: 25,
    height: 25,
    borderRadius: '100%',
    borderWidth: 1,
    borderColor: "#FF6C00",
  },
  imgBox: {
    position: "absolute",
    left: "35%",
    top: "-15%",
    width: 120,
    height: 120,
    backgroundColor: "#F6F6F6",
    borderRadius: 16,
  },
  title: {
    fontStyle: "normal",
    fontSize: 30,
    lineHeight: 35,
    letterSpacing: 0.16,
    color: "#212121",
    textAlign: "center",
    marginBottom: 20,
  },
});
